vibrioCholeraeOri = ""

def PatternCount(text, pattern):
    count = 0
    kLength = len(pattern)
    
    for i in range(0, len(text) - kLength + 1):
        if text[i:i + kLength] == pattern:
            count += 1
    return count


test = ""
sample = "AGACAGAAG"


x = PatternCount(test, sample)
print(x)